class SaleLR:
    def __init__(self, params, gamma=0.9, beta=0.9, eps=1e-8,
                 k_interval=20, power_iters=2):
        self.params=list(params); self.g=gamma; self.b=beta; self.eps=eps
        self.kint=k_interval; self.pit=power_iters; self.step=0; self.lam=0.
    @torch.no_grad()
    def _hessian_lambda(self, loss):
        v=[torch.randn_like(p) for p in self.params]
        v_norm=torch.sqrt(sum((x**2).sum() for x in v));
        v=[x/v_norm for x in v]
        for _ in range(self.pit):
            # finite-difference Hessian-vector product
            eps=1e-3
            for p,vi in zip(self.params,v): p.add_(vi,alpha=eps)
            loss_p=loss().detach();  # one fwd pass
            for p,vi in zip(self.params,v): p.add_(vi,alpha=-2*eps)
            loss_m=loss().detach();  # second fwd pass
            for p,vi in zip(self.params,v): p.add_(vi,alpha=eps)
            g=(loss_p-loss_m)/(2*eps)
            v=[gi.clone() for gi in torch.autograd.grad(g,self.params)]
            v_norm=torch.sqrt(sum((x**2).sum() for x in v));
            v=[x/v_norm for x in v]
        return v_norm.item()
    def lr(self, loss_cb):
        if self.step % self.kint==0:
            lam_hat=self._hessian_lambda(loss_cb)
            self.lam=self.b*self.lam+(1-self.b)*lam_hat
        self.step+=1
        return self.g/(self.lam+self.eps)

# GRAFF – core
class GNN(nn.Module):
    def __init__(self,d):
        super().__init__(); self.msg=nn.Linear(d*2,64); self.up=nn.GRUCell(64,d)
    def forward(self,x,adj):
        m=self.msg(torch.cat([x[adj[0]],x[adj[1]]],-1))
        agg=torch.zeros_like(x); agg.index_add_(0,adj[1],m)
        return self.up(agg.reshape(-1,x.size(-1)),x.reshape(-1,x.size(-1))).view_as(x)
class Graff(nn.Module):
    def __init__(self,blocks):
        super().__init__(); self.h=nn.Parameter(torch.zeros(len(blocks),16));
        self.gnn=GNN(16); self.W=nn.Linear(16,1)
    def forward(self,curv,entr,budget,adj):
        x=torch.cat([curv,entr,budget.expand_as(curv)],-1)
        self.h=self.gnn(self.h+x,adj)
        eta=torch.exp(self.W(self.h.mean(0)))
        m=torch.sigmoid(self.W(self.h)).squeeze()
        return eta,m

